import React from 'react';
//import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';

const EventCard = ({ event }) => {
  const navigate = useNavigate();

  // When card is clicked (except button), navigate to details page
  const handleCardClick = () => {
    navigate(`/events/${event._id}`);
  };

  // Prevent click event from bubbling to card when button is clicked
  const handleBookNowClick = (e) => {
    e.stopPropagation();
    // For now, navigate to event details page (or booking page)
    navigate(`/events/${event._id}`);
  };

  return (
    <div
  className="event-card"
      onClick={handleCardClick}
      style={{
        border: '1px solid #ccc',
        borderRadius: '8px',
        padding: '1rem',
        marginBottom: '1rem',
        cursor: 'pointer',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
      }}
      role="button"
      tabIndex={0}
      onKeyDown={(e) => {
        if (e.key === 'Enter' || e.key === ' ') {
          handleCardClick();
        }
      }}
    >
      {/* Only event name shown here */}
      <h3 style={{ margin: 0 }}>{event.title}</h3>

      {/* Book Now button */}
      <button type="button" onClick={handleBookNowClick}>
        Book Now
      </button>
    </div>
  );
};

export default EventCard;
