import React, { useEffect, useState, useContext } from 'react';
import { useParams } from 'react-router-dom';
import BookTicketForm from './BookTicketForm';
import { AuthContext } from '../auth/AuthContext';  // Your Auth context

const EventDetails = () => {
  const { id } = useParams();
  const { user, fetchEventById } = useContext(AuthContext);

  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadEvent = async () => {
      try {
        const response = await fetchEventById(id);
        setEvent(response.data.data);
      } catch {
        setError('Failed to load event details.');
      } finally {
        setLoading(false);
      }
    };
   loadEvent();
  }, [fetchEventById]);

if (loading) return <p>Loading event...</p>;
  if (error) return <p>{error}</p>;
  if (!event) return <p>Event not found.</p>;

  return (
    <div>
      <h2>{event.title}</h2>
      <p><strong>Date:</strong> {new Date(event.date).toLocaleString()}</p>
      <p><strong>Location:</strong> {event.location}</p>
      <p><strong>Description:</strong> {event.description}</p>
      <p><strong>Price:</strong> ${event.price.toFixed(2)}</p>
      <p><strong>Tickets Available:</strong> {event.ticketsAvailable}</p>

      {user ? (
        <BookTicketForm event={event} onBookingSuccess={() => alert('Thanks for booking!')} />
      ) : (
        <p>Please <a href="/login">log in</a> to book tickets.</p>
      )}
    </div>
  );
};

export default EventDetails;
// This component fetches and displays the details of a specific event.
// It also includes a booking form for logged-in users to book tickets.
