import React, { useContext, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'; // <-- import useParams here
import { AuthContext } from '../auth/AuthContext';
import BookTicketForm from './BookTicketForm';

const EventDetails = () => {
//   const { eventId } = useParams(); // get id from URL
//   console.log('Event ID from URL:', eventId); // add this log to debug

  const { fetchEventById, user } = useContext(AuthContext);

  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

useEffect(() => {
  console.log('Loading event with ID:', eventId);
  const loadEvent = async () => {
    try {
      const data = await fetchEventById(eventId);
      console.log('Event data from API:', data);
      if (!data || !data.data) {
        setError('Event not found.');
        setEvent(null);
      } else {
        setEvent(data.data);
      }
    } catch (err) {
      console.error('Error loading event:', err);
      setError('Failed to load event details.');
      setEvent(null);
    } finally {
      setLoading(false);
    }
  };
  loadEvent();
}, [eventId, fetchEventById]);


  if (loading) return <p>Loading event details...</p>;
  if (error) return <p>{error}</p>;
  if (!event) return <p>Event not found.</p>;

  return (
    <div style={{ padding: '2rem', maxWidth: '800px', margin: 'auto' }}>
      <h1>{event.title}</h1>
      <p><strong>Date:</strong> {new Date(event.date).toLocaleString()}</p>
      <p><strong>Location:</strong> {event.location}</p>
      <p><strong>Price:</strong> ${event.price.toFixed(2)}</p>
      <p><strong>Description:</strong> {event.description}</p>
      <p><strong>Timing:</strong> {event.timing || 'N/A'}</p>

     {user ? (
  <BookTicketForm event={event} onBookingSuccess={() => alert('Thanks for booking!')} />
) : (
  <p>Please <a href="/login">log in</a> to book tickets.</p>
)}

    </div>
  );
};

export default EventDetails;
